const axios = require('axios');
const fs = require('fs-extra');
const path = require('path');

// Define the wireframes directory
const WIREFRAMES_DIR = path.join(__dirname, 'wireframe');

class Orchestrator {
    constructor(grok) {
        this.queue = [];
        this.baseUrl = 'http://localhost:3000'; // Adjust as needed
        this.grok = grok; // Grok instance passed from server.js
        // Ensure the wireframes directory exists
        fs.ensureDirSync(WIREFRAMES_DIR);
    }

    enqueue(task) {
        this.queue.push(task);
    }

    async processQueue() {
        const results = {};
        while (this.queue.length > 0) {
            const task = this.queue.shift();
            console.log(`Executing task: ${task.name} with data:`, task.data);
            try {
                const result = await this.executeTask(task, results);
                results[task.name] = result;
                console.log(`Result for ${task.name}:`, result);

                // Write result to Markdown file
                await this.writeToMarkdown(task, result);

                if (task.next) {
                    let previousResult = result;
                    let previousRequirements = task.name.includes('analyze') ? result : null;
                    for (const nextTask of task.next) {
                        const updatedData = { ...nextTask.data };
                        if (nextTask.data.requirements === null) {
                            updatedData.requirements = previousRequirements || results[`analyze-${task.name.split('-')[1]}`];
                        }
                        if (nextTask.data.components === null && task.name.includes('decompose')) {
                            updatedData.components = previousResult;
                        }
                        if (nextTask.data.screens === null && task.name.includes('identify')) {
                            updatedData.screens = previousResult;
                        }

                        nextTask.data = updatedData;
                        console.log(`Processing next task: ${nextTask.name} with updated data:`, nextTask.data);

                        const nextResult = await this.executeTask(nextTask, results);
                        results[nextTask.name] = nextResult;
                        console.log(`Result for ${nextTask.name}:`, nextResult);

                        // Write next task result to Markdown file
                        await this.writeToMarkdown(nextTask, nextResult);

                        previousResult = nextResult;

                        if (nextTask.next) {
                            let nestedPreviousResult = nextResult;
                            for (const nestedTask of nextTask.next) {
                                const nestedData = { ...nestedTask.data };
                                if (nestedData.requirements === null) {
                                    nestedData.requirements = previousRequirements || results[`analyze-${task.name.split('-')[1]}`];
                                }
                                if (nestedData.components === null && nextTask.name.includes('decompose')) {
                                    nestedData.components = nestedPreviousResult;
                                }
                                if (nestedData.screens === null && nextTask.name.includes('identify')) {
                                    nestedData.screens = nestedPreviousResult;
                                }

                                nestedTask.data = nestedData;
                                console.log(`Processing nested task: ${nestedTask.name} with updated data:`, nestedTask.data);
                                const nestedResult = await this.executeTask(nestedTask, results);
                                results[nestedTask.name] = nestedResult;
                                console.log(`Result for ${nestedTask.name}:`, nestedResult);

                                // Write nested task result to Markdown file
                                await this.writeToMarkdown(nestedTask, nestedResult);

                                nestedPreviousResult = nestedResult;
                            }
                        }
                    }
                }
            } catch (error) {
                console.error(`Failed task ${task.name}:`, error.response ? error.response.data : error.message);
                throw error;
            }
        }
        return results;
    }

    async writeToMarkdown(task, result) {
        // Use epicName from task.data, default to 'default-epic' if not provided
        const epicName = task.data.epicName || 'default-epic';
        const filePath = path.join(WIREFRAMES_DIR, `${epicName}.md`);

        // Format the result as a string (handle JSON or plain text)
        let resultString = typeof result === 'object' ? JSON.stringify(result, null, 2) : result;

        // Prepare Markdown content
        const markdownContent = `## ${task.name}\n\`\`\`\n${resultString}\n\`\`\`\n\n`;

        // Append to the file (creates it if it doesn't exist)
        await fs.appendFile(filePath, markdownContent);
        console.log(`Wrote ${task.name} result to ${filePath}`);
    }

    async executeTask(task, previousResults) {
        const { name, data } = task;
        let prompt;

        switch (true) {
            case name.includes('analyze'):
                prompt = `Given the requirement: "${data.requirement.description}" (Benefit: ${data.requirement.benefit}, Objective: ${data.requirement.objective}, Value: ${data.requirement.value}), categorize it by functionality (e.g., UI, Data, Integration). Return only the JSON array with one object containing id, category, description, benefit, objective, and value. Preserve the original id if provided.`;
                break;
            case name.includes('decompose'):
                prompt = `Given the requirements: ${JSON.stringify(data.requirements)}, decompose the system into components (frontend, backend, database). Return a JSON array of component objects with frontend (e.g., screen.html), backend (e.g., service.js), and database (e.g., model name) fields.`;
                break;
            case name.includes('map'):
                prompt = `Given the requirements: ${JSON.stringify(data.requirements)} and components: ${JSON.stringify(data.components)}, create a traceability matrix linking each requirement to its components. Return a JSON array of objects with reqId and component fields.`;
                break;
            case name.includes('identify'):
                prompt = `Given the requirements: ${JSON.stringify(data.requirements)} and components: ${JSON.stringify(data.components)}, identify UI screens needed. Return a JSON array of screen objects with name, purpose, and reqId fields for UI-related requirements.`;
                break;
            case name.includes('generate'):
                prompt = `Given the screens: ${JSON.stringify(data.screens)}, generate an HTML5 wireframe with inline CSS for the first screen. Follow a simple layout with header, sidebar, main content, and footer. Return the HTML code as a plain string. Save it to public/${data.screens[0].name}-v1.html.`;
                break;
            default:
                throw new Error(`Unknown task: ${name}`);
        }

        try {
            const response = await this.grok.callGrok(prompt);
            const result = response.data.choices[0].message.content.trim().replace(/```json\n|```/g, '');

            // For generate task, save wireframe to file (keeping original behavior)
            if (name.includes('generate')) {
                const filename = `${data.screens[0].name}-v1.html`;
                await fs.writeFile(`public/${filename}`, result);
                return { filename };
            }

            return result;
        } catch (error) {
            throw new Error(`Task ${name} failed: ${error.message}`);
        }
    }
}

module.exports = Orchestrator;

const fs = require('fs-extra');
const path = require('path');
const puppeteer = require('puppeteer');
require('dotenv').config();

const WIREFRAMES_DIR = process.env.WIREFRAMES_DIR || './wireframes';

class WireframeGenerator {
    constructor(grok) {
        this.grok = grok;
        fs.ensureDirSync(WIREFRAMES_DIR);
    }

    async generateWireframe(requirement, wireframeName, previousVersion = null) {
        const prompt = previousVersion 
            ? `Given the previous wireframe version at "${previousVersion}" and the requirement: "${requirement.description}" (Benefit: ${requirement.benefit}, Objective: ${requirement.objective}, Value: ${requirement.value}), refine the wireframe HTML5 code based on the correction prompt provided in the requirement. Use a simple, standard web design layout with a header, sidebar, main content, and footer. Return only the HTML5 code with inline CSS as a plain string, no extra text or comments.`
            : `Given the requirement: "${requirement.description}" (Benefit: ${requirement.benefit}, Objective: ${requirement.objective}, Value: ${requirement.value}), generate a wireframe HTML5 page for a web application. Use a simple, standard web design layout with a header, sidebar (for navigation or context), main content (for primary interaction), and footer. Include inline CSS for basic styling (e.g., layout, colors). Return only the HTML5 code as a plain string, no extra text or comments.`;

        const response = await this.grok.callGrok(prompt);
        const htmlContent = response.data.choices[0].message.content.trim();

        // Sanitize and use wireframeName as base filename
        const baseFilename = wireframeName
            .toLowerCase()
            .replace(/\s+/g, '-') // Replace spaces with hyphens
            .replace(/[^a-z0-9-]/g, '') // Remove special characters
            .substring(0, 30); // Limit to 30 characters
        const version = await this.getNextVersion(baseFilename);
        const filename = `${baseFilename}-v${version}.html`;
        const filepath = path.join(WIREFRAMES_DIR, filename);

        await fs.writeFile(filepath, htmlContent);

        const screenshotFilename = `${baseFilename}-v${version}.png`;
        const screenshotPath = await this.captureScreenshot(filepath, screenshotFilename);

        return { filename, screenshotFilename, version };
    }

    async getNextVersion(baseFilename) {
        const files = await fs.readdir(WIREFRAMES_DIR);
        const versions = files
            .filter(f => f.startsWith(baseFilename) && f.endsWith('.html'))
            .map(f => parseInt(f.match(/v(\d+)\.html$/)[1]))
            .sort((a, b) => b - a);
        return versions.length ? versions[0] + 1 : 1;
    }

    async captureScreenshot(htmlPath, screenshotFilename) {
        const browser = await puppeteer.launch({ headless: true });
        const page = await browser.newPage();
        await page.setViewport({ width: 1280, height: 720 });
        await page.goto(`file://${path.resolve(htmlPath)}`, { waitUntil: 'networkidle0' });
        const screenshotPath = path.join(WIREFRAMES_DIR, screenshotFilename);
        await page.screenshot({ path: screenshotPath });
        await browser.close();
        return screenshotPath;
    }

    async listVersions(baseFilename) {
        const files = await fs.readdir(WIREFRAMES_DIR);
        return files
            .filter(f => f.startsWith(baseFilename) && f.endsWith('.html'))
            .map(f => ({
                filename: f,
                version: parseInt(f.match(/v(\d+)\.html$/)[1]),
                screenshot: f.replace('.html', '.png')
            }))
            .sort((a, b) => a.version - b.version);
    }
}

module.exports = WireframeGenerator;

const fs = require('fs');
const path = require('path');
const azureDevOpsAPI = require('./azure-devops');
const WIREFRAMES_DIR = process.env.WIREFRAMES_DIR || './wireframes';

function parseMarkdown(markdown) {
    try{
        const sections = {};
        let currentSection = null;
        const lines = markdown.split('\n');
        
        lines.forEach(line => {
            if (line.startsWith('## ')) {
                currentSection = line.substring(3).trim();
                sections[currentSection] = '';
            } else if (currentSection) {
                sections[currentSection] += line + '\n';
            }
        });
    
        const data = {};
        for (const section in sections) {
            const match = section.match(/(analyze|decompose|map)-(\d+)/);
            if (match) {
                const type = match[1];
                const index = match[2];
                if (!data[index]) data[index] = {};
                const content = sections[section];
                const jsonContent = extractJsonFromSection(content);
                data[index][type] = jsonContent;
            }
        }
        return data;
    
    } catch(error){
        throw new Error(`Failed to reading Markdown: ${error.message}`);
    }
}

function extractJsonFromSection(content) {
    const codeBlockMatch = content.match(/```(.*?)```/s);
    if (!codeBlockMatch) throw new Error('No code block found in section');
    const codeBlock = codeBlockMatch[1].trim();

    // Find JSON boundaries
    const startIndex = codeBlock.search(/[{[]/);
    if (startIndex === -1) throw new Error('No JSON object or array found');
    const endIndex = Math.max(codeBlock.lastIndexOf('}'), codeBlock.lastIndexOf(']'));
    if (endIndex === -1) throw new Error('No closing bracket found');

    const jsonString = codeBlock.substring(startIndex, endIndex + 1);
    console.log('Extracted JSON string:', jsonString); // Log for verification

    try {
        return parseJsonWithComments(jsonString);
    } catch (error) {
        throw new Error(`Failed to parse JSON in section: ${error.message}`);
    }
}

function parseJsonWithComments(jsonString) {
    // Remove single-line comments (// comment)
    const cleanedString = jsonString.replace(/\/\/.*$/gm, '');
    
    // Parse the cleaned JSON
    try {
        return JSON.parse(cleanedString);
    } catch (error) {
        throw new Error(`Failed to parse JSON: ${error.message}`);
    }
}

function getComponentNames(components, key) {
    if (!components[key]) return [];
    return components[key].map(item => typeof item === 'string' ? item : item.name || item.file || 'unknown');
}

async function createWorkItems(epicName) {
    const markdownPath = path.join(WIREFRAMES_DIR, `${epicName}.md`);
    if (!fs.existsSync(markdownPath)) {
        throw new Error(`Markdown file for epic ${epicName} not found`);
    }
    const markdown = fs.readFileSync(markdownPath, 'utf8');
    const data = parseMarkdown(markdown);

    // Create Epic
    const epicDescription = 'Build a system to manage family chores, enabling user registration, family creation, chore logging, assignment, and visibility control, with offline support and custom lists for enhanced coordination and privacy.';
    const epic = await azureDevOpsAPI.createWorkItem('Epic', epicName, epicDescription, 'Product Owner');
    const epicId = epic.id;

    // Create Feature under Epic
    const featureDescription = 'Deliver the core capabilities of the Family Chore Management System, including user authentication, family management, chore management, and system features like offline support and data privacy.';
    const feature = await azureDevOpsAPI.createWorkItem('Feature', 'Implement Core Functionalities', featureDescription, 'Product Owner', epicId);
    const featureId = feature.id;

    // Process each requirement
    for (const index in data) {
        const requirement = data[index]['analyze'][0]; // Assuming single requirement per section
        const components = data[index]['decompose'][0]; // Assuming single decomposition per section

        // Create User Story
        const userStoryTitle = `Implement requirement ${requirement.id}: ${requirement.description}`;
        const userStoryDescription = `
Requirement ID: ${requirement.id}
Category: ${requirement.category}
Description: ${requirement.description}
Benefit: ${requirement.benefit}
Objective: ${requirement.objective}
Value: ${requirement.value}
        `.trim();
        const userStory = await azureDevOpsAPI.createWorkItem('User Story', userStoryTitle, userStoryDescription, 'Product Owner', featureId);
        const userStoryId = userStory.id;

        // Extract component names
        const frontendNames = getComponentNames(components, 'frontend');
        const backendNames = getComponentNames(components, 'backend');
        const databaseNames = getComponentNames(components, 'database');

        // Create Tasks
        await azureDevOpsAPI.createWorkItem('Task', 'Create Wireframes', `Design wireframes for ${frontendNames.join(', ')}`, 'UX Designer', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Define System Architecture', `Outline components: frontend (${frontendNames.join(', ')}), backend (${backendNames.join(', ')}), database (${databaseNames.join(', ')})`, 'Solution Architect', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Create Technical Specifications', `Specify services and models based on decomposition`, 'Tech Lead', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Implement Frontend', `Develop ${frontendNames.join(', ')}`, 'Developer Lead', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Implement Backend', `Develop ${backendNames.join(', ')}`, 'Developer Lead', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Set Up Database', `Create models: ${databaseNames.join(', ')}`, 'Developer Lead', userStoryId);
        await azureDevOpsAPI.createWorkItem('Task', 'Create Test Cases', `Develop test scenarios based on requirement ${requirement.id}`, 'QA Lead', userStoryId);
    }

    return { message: 'Work items created successfully' };
}

module.exports = { createWorkItems };
